import React, { useState, useEffect } from 'react';
import { useAuth } from '../../hooks';

function AdminUsers({ navigateTo }) {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    role: 'all',
    status: 'all'
  });
  const { api } = useAuth();

  useEffect(() => {
    fetchUsers();
  }, [filters]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await api.get('/admin/users');
      let usersData = response.data || response;

      // Apply filters
      if (filters.search) {
        usersData = usersData.filter(user => 
          user.name?.toLowerCase().includes(filters.search.toLowerCase()) ||
          user.email?.toLowerCase().includes(filters.search.toLowerCase())
        );
      }

      if (filters.role !== 'all') {
        usersData = usersData.filter(user => 
          user.roles?.includes(filters.role)
        );
      }

      setUsers(usersData);
    } catch (error) {
      console.error('Error fetching users:', error);
      // Set fallback data
      setUsers([
        {
          id: 1,
          name: 'Johnny Rodriguez',
          email: 'jhonny@ar-mediia.com',
          roles: ['admin'],
          created_at: '2024-01-15T10:30:00Z',
          last_login: '2025-01-27T14:20:00Z',
          status: 'active',
          avatar: 'üõ°Ô∏è'
        },
        {
          id: 2,
          name: 'TenZ Gaming',
          email: 'tenz@gaming.com',
          roles: ['user'],
          created_at: '2024-02-10T16:45:00Z',
          last_login: '2025-01-27T12:15:00Z',
          status: 'active',
          avatar: 'üéÆ'
        },
        {
          id: 3,
          name: 'Moderator Mike',
          email: 'mike@mrvl.com',
          roles: ['moderator'],
          created_at: '2024-01-20T09:15:00Z',
          last_login: '2025-01-26T18:30:00Z',
          status: 'active',
          avatar: '‚≠ê'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (userId, userName) => {
    if (window.confirm(`Are you sure you want to delete user "${userName}"? This action cannot be undone.`)) {
      try {
        // FIXED: Use POST with method spoofing for Laravel backend deletes
        await api.post(`/admin/users/${userId}`, { _method: 'DELETE' });
        await fetchUsers(); // Refresh the list
        alert('User deleted successfully!');
      } catch (error) {
        console.error('Error deleting user:', error);
        alert('Error deleting user. Please try again.');
      }
    }
  };

  const updateUserRole = async (userId, newRole) => {
    try {
      // ‚úÖ FIXED: Optimistic UI update for immediate feedback - handle both role formats
      setUsers(prevUsers => 
        prevUsers.map(user => 
          user.id === userId 
            ? { 
                ...user, 
                roles: [{ name: newRole }] // Backend expects role objects with name property
              }
            : user
        )
      );

      // Get current user data to preserve status
      const currentUser = users.find(u => u.id === userId);
      await api.put(`/admin/users/${userId}`, { 
        role: newRole,
        status: currentUser.status || 'active'
      });

      // Force refresh after successful update
      await fetchUsers();
      alert(`‚úÖ User role updated to ${newRole}!`);
    } catch (error) {
      console.error('‚ùå Error updating user role:', error);
      // Revert optimistic update on error
      await fetchUsers();
      alert('‚ùå Error updating user role. Please try again.');
    }
  };

  const updateUserStatus = async (userId, newStatus) => {
    try {
      // FIXED: Get current user data to preserve role
      const currentUser = users.find(u => u.id === userId);
      const currentRole = currentUser.roles?.[0] || 'user';
      await api.put(`/admin/users/${userId}`, { 
        role: currentRole, 
        status: newStatus 
      });
      await fetchUsers(); // Refresh the list
      alert(`User status updated to ${newStatus}!`);
    } catch (error) {
      console.error('Error updating user status:', error);
      alert('Error updating user status. Please try again.');
    }
  };

  const getRoleColor = (roles) => {
    if (roles?.includes('admin')) return 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400';
    if (roles?.includes('moderator')) return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400';
    return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300';
  };

  const getRoleDisplay = (roles) => {
    if (roles?.includes('admin')) return 'üõ°Ô∏è Admin';
    if (roles?.includes('moderator')) return '‚≠ê Moderator';
    return 'üë§ User';
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400';
      case 'inactive': return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300';
      case 'banned': return 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const formatLastLogin = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.abs(now - date) / (1000 * 60 * 60);

    if (diffInHours < 1) return 'Just now';
    if (diffInHours < 24) return `${Math.floor(diffInHours)}h ago`;
    if (diffInHours < 48) return 'Yesterday';
    return `${Math.floor(diffInHours / 24)}d ago`;
  };

  const roles = ['all', 'admin', 'moderator', 'user'];
  const statuses = ['all', 'active', 'inactive', 'banned'];

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="loading-spinner mx-auto mb-4"></div>
          <div className="text-gray-600 dark:text-gray-400">Loading users...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold text-gray-900 dark:text-white">Manage Users</h2>
          <p className="text-gray-600 dark:text-gray-400">Manage user accounts, roles, and permissions</p>
        </div>
        <button 
          onClick={() => navigateTo('admin-user-create')}
          className="btn btn-primary"
        >
          üë§ Create New User
        </button>
      </div>

      {/* User Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="card p-6 text-center">
          <div className="text-3xl mb-2">üë•</div>
          <div className="text-2xl font-bold text-blue-600 dark:text-blue-400">
            {users.length}
          </div>
          <div className="text-sm text-gray-600 dark:text-gray-400">Total Users</div>
        </div>
        <div className="card p-6 text-center">
          <div className="text-3xl mb-2">üõ°Ô∏è</div>
          <div className="text-2xl font-bold text-red-600 dark:text-red-400">
            {users.filter(u => u.roles?.includes('admin')).length}
          </div>
          <div className="text-sm text-gray-600 dark:text-gray-400">Admins</div>
        </div>
        <div className="card p-6 text-center">
          <div className="text-3xl mb-2">‚≠ê</div>
          <div className="text-2xl font-bold text-yellow-600 dark:text-yellow-400">
            {users.filter(u => u.roles?.includes('moderator')).length}
          </div>
          <div className="text-sm text-gray-600 dark:text-gray-400">Moderators</div>
        </div>
        <div className="card p-6 text-center">
          <div className="text-3xl mb-2">‚úÖ</div>
          <div className="text-2xl font-bold text-green-600 dark:text-green-400">
            {users.filter(u => u.status === 'active').length}
          </div>
          <div className="text-sm text-gray-600 dark:text-gray-400">Active Users</div>
        </div>
      </div>

      {/* Filters */}
      <div className="card p-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium mb-2 text-gray-900 dark:text-gray-100">
              Search Users
            </label>
            <input
              type="text"
              placeholder="Search by name or email..."
              value={filters.search}
              onChange={(e) => setFilters({...filters, search: e.target.value})}
              className="form-input"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-2 text-gray-900 dark:text-gray-100">
              Role
            </label>
            <select
              value={filters.role}
              onChange={(e) => setFilters({...filters, role: e.target.value})}
              className="form-input"
            >
              {roles.map(role => (
                <option key={role} value={role}>
                  {role === 'all' ? 'All Roles' : role.charAt(0).toUpperCase() + role.slice(1)}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium mb-2 text-gray-900 dark:text-gray-100">
              Status
            </label>
            <select
              value={filters.status}
              onChange={(e) => setFilters({...filters, status: e.target.value})}
              className="form-input"
            >
              {statuses.map(status => (
                <option key={status} value={status}>
                  {status === 'all' ? 'All Status' : status.charAt(0).toUpperCase() + status.slice(1)}
                </option>
              ))}
            </select>
          </div>
          <div className="flex items-end">
            <button
              onClick={() => setFilters({ search: '', role: 'all', status: 'all' })}
              className="btn btn-secondary w-full"
            >
              Clear Filters
            </button>
          </div>
        </div>
      </div>

      {/* Users Table */}
      <div className="card overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead className="bg-gray-50 dark:bg-gray-800">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  User
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  Role
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  Last Login
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  Joined
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white dark:bg-gray-900 divide-y divide-gray-200 dark:divide-gray-700">
              {users.map((user) => (
                <tr key={user.id} className="hover:bg-gray-50 dark:hover:bg-gray-800">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="text-2xl mr-3">{user.avatar}</div>
                      <div>
                        <div className="text-sm font-medium text-gray-900 dark:text-white">
                          {user.name}
                        </div>
                        <div className="text-sm text-gray-500 dark:text-gray-400">
                          {user.email}
                        </div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getRoleColor(user.roles)}`}>
                      {getRoleDisplay(user.roles)}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(user.status)}`}>
                      {user.status?.charAt(0).toUpperCase() + user.status?.slice(1)}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                    {user.last_login ? formatLastLogin(user.last_login) : 'Never'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                    {formatDate(user.created_at)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2">
                      <select
                        value={user.roles?.[0]?.name || user.roles?.[0] || 'user'}
                        onChange={(e) => updateUserRole(user.id, e.target.value)}
                        className="text-xs border border-gray-300 dark:border-gray-600 rounded px-2 py-1 bg-white dark:bg-gray-800"
                      >
                        <option value="user">User</option>
                        <option value="moderator">Moderator</option>
                        <option value="admin">Admin</option>
                      </select>
                      <select
                        value={user.status}
                        onChange={(e) => updateUserStatus(user.id, e.target.value)}
                        className="text-xs border border-gray-300 dark:border-gray-600 rounded px-2 py-1 bg-white dark:bg-gray-800"
                      >
                        <option value="active">Active</option>
                        <option value="inactive">Inactive</option>
                        <option value="banned">Banned</option>
                      </select>
                      <button
                        onClick={() => handleDelete(user.id, user.name)}
                        className="text-red-600 hover:text-red-900 dark:text-red-400 dark:hover:text-red-300"
                        disabled={user.roles?.includes('admin')} // Prevent deleting admins
                      >
                        üóëÔ∏è
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* No Results */}
      {users.length === 0 && (
        <div className="card p-12 text-center">
          <div className="text-6xl mb-4">üë§</div>
          <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">No Users Found</h3>
          <p className="text-gray-600 dark:text-gray-400 mb-4">
            {filters.search || filters.role !== 'all' || filters.status !== 'all'
              ? 'Try adjusting your filters to find more users.'
              : 'No users in the system yet.'}
          </p>
          <button
            onClick={() => navigateTo('admin-user-create')}
            className="btn btn-primary"
          >
            üë§ Create First User
          </button>
        </div>
      )}
    </div>
  );
}

export default AdminUsers;
