  const handleMatchClick = (matchId) => {
    console.log('🔗 EventDetailPage: Navigating to match with REAL ID:', matchId);
    navigateTo && navigateTo('match-detail', { id: matchId });
  };

  const handleTeamClick = (teamId) => {
