    { id: 'matches', name: 'Matches', icon: '⚔️' },
    { id: 'teams', name: 'Teams', icon: '👥' },
    { id: 'bracket', name: 'Bracket', icon: '🏆' },
    { id: 'schedule', name: 'Schedule', icon: '📅' }
